import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { CategoryModel } from 'src/app/models/category.model';
import { EventModel } from 'src/app/models/event.model';
import { DataService } from 'src/app/services/data.service';
import { Router } from '@angular/router';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-insert',
  standalone: true,
  imports: [CommonModule,FormsModule],
  templateUrl: './insert.component.html',
  styleUrls: ['./insert.component.css']
})
export class InsertComponent implements OnInit {

    public categories: CategoryModel[];
    public event = new EventModel();

    public constructor(
        private dataService: DataService,
        private router: Router) { }

    public async ngOnInit() {
        try {
            this.categories = await this.dataService.getAllCategories();
        }
        catch (err: any) {
            alert(err.message);
        }
    }

    public async send() {
        try {
            await this.dataService.addEvent(this.event);
            alert("Even has been added.");
            this.router.navigateByUrl("/list");
        }
        catch (err: any) {
            alert(err.message);
        }
    }

}
<div>
    <form (submit)="send()" ngNativeValidate>

        <label>Select Category:</label>
        <select name="categoryId" [(ngModel)]="event.categoryId" required>
            <option disabled selected>Select Category</option>
            <option *ngFor="let c of categories" [value]="c._id">{{c.categoryName}}</option>
        </select>

        <label>Event date and time:</label>
        <input type="datetime-local" name="date" [(ngModel)]="event.eventDate" required>

        <label>Description:</label>
        <input type="text" name="description" [(ngModel)]="event.eventDescription" required minlength="2" maxlength="100">

        <label>Address:</label>
        <input type="text" name="address" [(ngModel)]="event.eventAddress" required minlength="2" maxlength="100">

        <label>People amount:</label>
        <input type="number" name="people_amount" [(ngModel)]="event.eventAmountPeople" required min="5" max="1000">

        <button>Add</button>

    </form>
</div>
